              groupValue: _group2,
              onChanged: (Group2? value) {
                setState(() {
                  _group2 = value;
                });
              }),
          VRadio<Group2>(
              style: VRadioStyle(
                  fillColor: displayError
                      ? VColors.negativeGraphics
                      : VColors.defaultActive),
              title: "Label 3",
              value: Group2.g2l3,
              groupValue: _group2,
              onChanged: (Group2? value) {
                setState(() {
                  _group2 = value;
                });
              }),
          const SizedBox(
            height: 5,
          ),
          ExcludeSemantics(
            child: Visibility(
              visible: displayError,
              child: Text.rich(
                  TextSpan(style: defaultVTheme.textStyles.uiLabel, children: [
                const WidgetSpan(
                    child: VIcon(
                  svgIcon: VIcons.errorTiny,
                  iconWidth: 18,
                  iconHeight: 18,
                  iconColor: VColors.negativeGraphics,
                )),
                const WidgetSpan(
                    child: Padding(padding: EdgeInsets.only(left: 5))),
                TextSpan(
                    text: errorMessage,
                    style: const TextStyle(color: VColors.negativeText))
              ])),
            ),
          ),
          const SizedBox(
            height: 8,
          ),
          VButton(
            onPressed: () async {
              if (_group2 != null) {
                setState(() {
                  displayError = false;
                });
              } else {
                setState(() {
                  displayError = true;
                });
                SemanticsService.announce(errorMessage, TextDirection.ltr);
              }
            },
            child: const Text("Submit"),
          )
        ]);
  }

